mapper.update:
update task_server
set name = #{name,jdbcType=VARCHAR},
  ip = #{ip,jdbcType=VARCHAR},
  port = #{port,jdbcType=VARCHAR},
  remark = #{remark,jdbcType=VARCHAR}
where guid = #{guid,jdbcType=INTEGER}

mapper.select:
select guid, name, ip, port, remark
from task_server
where guid = #{guid,jdbcType=INTEGER}
